import com.eviware.soapui.support.XmlHolder
import jxl.*
import jxl.write.*
def response = testRunner.testCase.getTestStepByName('GetList').getPropertyValue("Response")
log.info " all response $response"
def resultsXml = new XmlSlurper().parseText("$response")
def firstname = context.testCase.getTestStepByName("Customer Properties").getPropertyValue("firstname")
log.info "firstname is $firstname"
def ids = resultsXml.'**'.find{it.firstname.text() == firstname}.collect{ it.id.text() } 
log.info "ids $ids"
StringBuilder sb = new StringBuilder();
String id = ids.join()
log.info "id is $id"
context.testCase.getTestStepByName("Customer Properties").setPropertyValue("id",id)

con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder

def response = context.getProperty("Response")
def holder = new XmlHolder(response)
def responseEmail = holder.getNodeValue("//email")
log.info "Data retrieved from response is $responseEmail"
def customerEmail = context.testCase.getTestStepByName("Customer Properties").getPropertyValue("email")
log.info "Data retrieved from property is $customerEmail"
assert responseEmail.contains(customerEmail)
if(responseEmail == customerEmail)
{
log.info "Test Case Passed"
}
else
{
	log.info "Test Case Failed"
}

con:config.script:
import jxl.*

String filePath = "C:/Users/RaviChandraSurabhi/Desktop/DataSheet.xls"

File Excel = new File(filePath)
Workbook wb = Workbook.getWorkbook(Excel)
Sheet sh = wb.getSheet("Sheet1")

def rowsize = sh.getRows().toInteger()
def columnsize = sh.getColumns().toInteger()
def email
def firstname
def lastname
log.info "The total number of rows are :" +rowsize
log.info "The total number of columns are :" +columnsize
for (def i=0;i<rowsize;i++){
        for(def j=0;j<columnsize;j++){
        	if(j==0)
        	{
        	 email = sh.getCell(j,i).getContents()
        	}
        	else if(j==1)
        	{
        	firstname = sh.getCell(j,i).getContents()
        	}
        	else
        	{
        		lastname = sh.getCell(j,1).getContents();
        	}
     }
}

     log.info email
     log.info firstname
     log.info lastname

def tc = testRunner.testCase.testSuite.project.testSuites['TestSuite_DataDriven'].testCases['TC001']
tc.testSteps["Customer Properties"].setPropertyValue("email",email)
tc.testSteps["Customer Properties"].setPropertyValue("firstname",firstname)
tc.testSteps["Customer Properties"].setPropertyValue("lastname",lastname)

